.block {
  border-top: 1px solid #3C4EFA;
  border-right: 0px solid #3C4EFA;
  border-bottom: 0px solid #3C4EFA;
  border-left: 0px solid #3C4EFA;
  border-color: rgba(60, 78, 250, 0.3);
}

.block {
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  line-height: 1.5;
}

.block {
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: flex-start;
  gap: 4px;
}

.block {
  display: inline-flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: flex-start;
  gap: 4px;
}

@mixin placeholder($color) {
  &::-webkit-input-placeholder { /* WebKit, Blink, Edge */
    color: $color;
  }
  &:-moz-placeholder { /* Mozilla Firefox 4 to 18 */
    color: $color;
    opacity: 1;
  }
  &::-moz-placeholder { /* Mozilla Firefox 19+ */
    color: $color;
    opacity: 1;
  }
  &:-ms-input-placeholder { /* Internet Explorer 10-11 */
    color: $color;
  }
  &::-ms-input-placeholder { /* Microsoft Edge */
    color: $color;
  }
  &::placeholder { /* Most modern browsers support this now. */
    color: $color;
  }
}

input::-webkit-input-placeholder {
  color: #0095FF;
}

input:-moz-placeholder {
  color: #0095FF;
  opacity: 1;
}

input::-moz-placeholder {
  color: #0095FF;
  opacity: 1;
}

input:-ms-input-placeholder {
  color: #0095FF;
}

input::-ms-input-placeholder {
  color: #0095FF;
}

input::placeholder {
  color: #0095FF;
}

.block {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: auto;
  gap: 4px;
}

$scrollbarBg: $white;
$scrollbarThumb: $pages;
$scrollbarWidth: $zui-4;
$scrollbarBorder: 1px solid $scrollbarBg;
$scrollbarBorderRadius: $zui-8;

@mixin scrollbar {
  scrollbar-width: $scrollbarWidth;
  scrollbar-height: $scrollbarWidth;
  scrollbar-color: $scrollbarThumb $scrollbarBg;

  &::-webkit-scrollbar {
    width: $scrollbarWidth;
    height: $scrollbarWidth;
  }

  &::-webkit-scrollbar-track {
    background: $scrollbarBg;
    border-radius: $scrollbarBorderRadius;
  }

  &::-webkit-scrollbar-thumb {
    background-color: $scrollbarThumb;
    border-radius: $scrollbarBorderRadius;
    border: $scrollbarBorder;
  }
}

@mixin transition($time: 500) {
  -webkit-transition: all #{$time}ms ease-in-out;
  -moz-transition: all #{$time}ms ease-in-out;
  -o-transition: all #{$time}ms ease-in-out;
  -ms-transition: all #{$time}ms ease-in-out;
  transition: all #{$time}ms ease-in-out;
}

.block {
  -webkit-transition: all 500ms ease-in-out;
  -moz-transition: all 500ms ease-in-out;
  -o-transition: all 500ms ease-in-out;
  -ms-transition: all 500ms ease-in-out;
  transition: all 500ms ease-in-out;
}

.block {
  -webkit-transition: all 250ms ease-in-out;
  -moz-transition: all 250ms ease-in-out;
  -o-transition: all 250ms ease-in-out;
  -ms-transition: all 250ms ease-in-out;
  transition: all 250ms ease-in-out;
}

@mixin transition-delay($time, $delay) {
  -webkit-transition: all #{$time}ms ease-in-out;
  -webkit-transition-delay: #{$delay}ms;
  -o-transition: all #{$time}ms ease-in-out;
  -o-transition-delay: #{$delay}ms;
  -moz-transition: all #{$time}ms ease-in-out;
  -moz-transition-delay: #{$delay}ms;
  transition: all #{$time}ms ease-in-out;
  transition-delay: #{$delay}ms;
}

.block {
  -webkit-transition: all 500ms ease-in-out;
  -webkit-transition-delay: 1000ms;
  -o-transition: all 500ms ease-in-out;
  -o-transition-delay: 1000ms;
  -moz-transition: all 500ms ease-in-out;
  -moz-transition-delay: 1000ms;
  transition: all 500ms ease-in-out;
  transition-delay: 1000ms;
}
